#!/bin/bash
# Script para crear iconos básicos para la aplicación

echo "Creando iconos placeholder..."

# Crear un PNG simple de 256x256 (icono principal)
cat > assets/icon.png.base64 << 'EOF'

EOF

# Decodificar base64 a PNG
base64 -d assets/icon.png.base64 > assets/icon.png
rm assets/icon.png.base64

# Copiar para crear tray icon (más pequeño)
cp assets/icon.png assets/tray-icon.png

echo "✓ Iconos creados en assets/"
echo "Nota: Son iconos placeholder. Reemplázalos con tus propios diseños para mejor apariencia."
